package SINTAXIS_BASICA;

public class OPERACION_ENTEROS {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int operant1;
		int operant2;
		int resultat;
		{
			operant1 = 17;
			operant2 = 3;
			resultat = operant1/operant2;
			System.out.println(resultat);
		}
		{
			operant1 = 9;
			operant2 = 3;
			resultat = operant1/operant2;
			System.out.println(resultat);
		}
		{
			operant1 = 21;
			operant2 = 4;
			resultat = operant1/operant2;
			System.out.println(resultat);
		}
		System.out.println(" ");
		{
			operant1 = 17;
			operant2 = 3;
			resultat = operant1%operant2;
			System.out.println(resultat);
		}
		{
			operant1 = 9;
			operant2 = 3;
			resultat = operant1%operant2;
			System.out.println(resultat);
		}
		{
			operant1 = 21;
			operant2 = 4;
			resultat = operant1%operant2;
			System.out.println(resultat);
		}
		System.out.println(" ");
		{
			double operant3 = 17;
			double operant4 = 3;
			double resultat1 = operant3%operant4;
			System.out.println(resultat1);
		}
		{
			double operant3 = 9;
			double operant4 = 3;
			double resultat1 = operant3%operant4;
			System.out.println(resultat1);
		}
		{
			double operant3 = 21;
			double operant4 = 4;
			double resultat1 = operant4%operant4;
			System.out.println(resultat1);
		}
	}

}
